    app.userStringInput = '113';
    expect(() => app.validateInput()).toThrow("[ERROR] 1부터 9사이의 숫자가 하나씩 있어야합니다.");
  });

  test("0은 입력될 수 없습니다.", () => {
    const app = new App();
    app.userStringInput = '019';
    expect(() => app.validateInput()).toThrow("[ERROR] 1부터 9사이의 숫자만 입력해야합니다.");
  })

  test("음수는 입력될 수 없습니다.", () => {
