    // 수정 이후
    const { userStringInput } = this;
    const uniqueArr = [...new Set([...input])];

    if(userStringInput.length !== 3) throw new Error(ERROR_MESSAGE.LENGTH);
    if(!REGEX.test(userStringInput)) throw new Error(ERROR_MESSAGE.INT);
